// SPDX-License-Identifier: MIT
pragma solidity ^0.8.8;

import './PriceConverter.sol';

error NotOwner();

contract FundMe {
    using PriceConverter for uint256;

    uint256 public constant minimumUSD = 50 * 1e18;
    address[] public funders;
    mapping(address => uint256) public addressToAmount;
    address public immutable owner;

    AggregatorV3Interface public priceFeed;

    constructor(
        address priceFeedAddress
    ) {
        owner = msg.sender;

        priceFeed = AggregatorV3Interface(priceFeedAddress);
    }

    function fund() public payable {
        require(msg.value.getConversionRate(priceFeed) >= minimumUSD, "Did not send enough eth");
        funders.push(msg.sender);
        addressToAmount[msg.sender] = msg.value;
    }

    function withdraw() public onlyOwner {
        for(/*start index, end condition, step */ uint256 funderIndex = 0; funderIndex < funders.length; funderIndex++) {
            address funder = funders[funderIndex];
            addressToAmount[funder] = 0;
        }

        funders = new address[](0);

        // transfer funds
        payable(msg.sender).transfer(address(this).balance);

        // send funds does not throw and revert, but returns a bool if successfull;
        bool isSuccess = payable(msg.sender).send(address(this).balance);
        require(isSuccess, "Send failed");

        // call 
        (     bool callSuccess,    /* bytes memory dataReturned */ ) = payable(msg.sender).call{
            value: address(this).balance
        }("");
        require(callSuccess, "Call failed");
    }

    receive() external payable {
        fund();
    }

    fallback() external payable {
        fund();
    }

    modifier onlyOwner {
        if (msg.sender != owner) {
            revert NotOwner();
        }
        // require(msg.sender == owner, "Sender is not owner");
        _;
    }
}

